import React, { useEffect, useState } from 'react'
import Navbar from '../../Navbar/Navbar'
import './Search.css'
import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from '@mui/icons-material/Close';
import axios from 'axios';


function Search() {

  const [foodItems,setFoodItems] = useState([]);

 

  const getData = async () => {
    await axios.get(`https://www.themealdb.com/api/json/v1/1/categories.php`).then((resp)=>{
      const data = resp.data.categories;
      setFoodItems(data);
    })
  }

  useEffect(()=>{
    getData();
   },[]);


   console.log("foodItems",foodItems);
  return (
    <div className='search_container'>
      <Navbar/>

      {/* Search Section */}
      <div className="search_main_container">
        <div className="inputField">
           <SearchIcon sx={
             {
               fontSize:"1.6vw"
             }
           }/>
           <input type="text" placeholder='Search For Restaurants or Dishes'/>
           <strong style={{
             fontSize:"0.7vw",
             color:"#fc8019",
             visibility:"hidden"
           }} className="clearText">CLEAR</strong>
        </div>

        <div className="cancelSearch">
          <button className='closeBtn'>
            <CloseIcon sx={
              {
                fontSize:"2.8vw",
                fontWeight:"100",
                border:"none"
              }
            }/>
            <span>ESC</span>
          </button>
        </div>
      </div>

      {/* Disability Section */}
       <div className="show_result">
         
       </div>
    </div>
  )
}

export default Search
